<script>
    function repositionDiagramLabels(ratio) {
        $(".label").each(function() {
            var e = $(this),
                t = e.data("originleft") * ratio,
                n = e.data("origintop") * ratio,
                r = e.data("originwidth") * ratio,
                i = e.data("originheight") * ratio;
            e.css("left", t + "px").css("top", n + "px").css("width", r + "px").css("height", i + "px")
        })
    }

    var img = document.getElementById('illustration');
    var e = document.getElementsByClassName('border');

    img.onload = function() {
        var ratio = img.width / img.naturalWidth;
        repositionDiagramLabels(ratio);
    }

    function handle(evnt) {
        var ratio = img.width / img.naturalWidth;
        repositionDiagramLabels(ratio);
        return true;
    }

    window.onresize = handle;
</script>